setup.source:
public class LocalVariableCouldBeFinal {

    public void foo() {
        int a = 0;
    }

}

expected.source:
public class LocalVariableCouldBeFinal {

    public void foo() {
        final int a = 0;
    }

}

setup.source:
public class LocalVariableCouldBeFinal {

    public void foo() {
        int a = 1, b = 2, c = 3;
    }

}

expected.source:
public class LocalVariableCouldBeFinal {

    public void foo() {
        final int a = 1, b = 2, c = 3;
    }

}
