setup.source:
class SingularField {
    private int i;
    void foo() {
        i=1;
    }
}

expected.source:
class SingularField {
    void foo() {
        final int i=1;
    }
}

setup.source:
class SingularField {
    private int i;
    void foo() {
        this.i=1;
    }
}

expected.source:
class SingularField {
    void foo() {
        final int i=1;
    }
}

