setup.source:
public class ByteInstantiation {

    Byte i = new Byte(123);

}

expected.source:
public class ByteInstantiation {

    Byte i = Byte.valueOf(123);

}

setup.source:
public class ByteInstantiation {

    Byte i = new Byte(123 + 4);

}

expected.source:
public class ByteInstantiation {

    Byte i = Byte.valueOf(123 + 4);

}
