setup.source:
public class LongInstantiation {

    Long i = new Long(123);

}

expected.source:
public class LongInstantiation {

    Long i = Long.valueOf(123);

}

setup.source:
public class LongInstantiation {

    Long i = new Long(123 + 4);

}

expected.source:
public class LongInstantiation {

    Long i = Long.valueOf(123 + 4);

}
