setup.source:
public class ShortInstantiation {

    Short i = new Short(123);

}

expected.source:
public class ShortInstantiation {

    Short i = Short.valueOf(123);

}

setup.source:
public class ShortInstantiation {

    Short i = new Short(123 + 4);

}

expected.source:
public class ShortInstantiation {

    Short i = Short.valueOf(123 + 4);

}
